import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { useLocation } from "react-router-dom"


const DoctorAppointmentsPage = ( ) => {
    const {state} = useLocation()
    const appointments = state.appointments
    const doctor = state.doctor
    const navigate = useNavigate()
    const [newDate, setDateValue] = useState(new Date())

    function getLocaleDateString() {
        const formats = {
            "sv-SE": "yyyy-MM-dd"
        }
    }
    const onLogOutSubmit = (e) => {
        e.preventDefault()

        navigate('/', {
        })
    }

    const Appointment = ({appointments, onClick}) => {
        return (
            <>
            {appointments.map((app) => 
            (
            <Appointment2 key = {app.idappointment} app = {app}
            onClick = {onClick} />
            )
            )
            }
            </>
        )
    }

    const Appointment2 = ({app, onClick}) => {
        var classN = new String("appointment")
        var g1 = new Date(app.date)
        var g2 = new Date()

        if (g1.getTime() < g2.getTime()){
            classN = "past-appointment"
        }
        console.log(app.patient)
        return(
            <div className = {classN}  onClick={() => onClick(app.idappointment)}>
                 <h3>
                Date:&nbsp;&nbsp;
                {app.date}&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;
                Time:&nbsp;&nbsp;
                {app.time}&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;
                Patient:&nbsp;&nbsp;
                {app.patient.fullName}
            </h3>
            </div>
        )
    }

    const writeThings = async (id) => {
        console.log(id)

        const appointment = appointments.find((app) => app.idappointment == id)

        const res = await fetch(`http://localhost:8080/patients`)
        const data = await res.json()

        const patient = data.find((patient) => patient.idpatient === appointment.patient)


        console.log(patient)
        navigate('/prescription-diagnostic', {
            state: {
                doctor : doctor,
                patient : patient
            }
     })
    }
    


    return (
        <div>
            <div class = "row">
                <div style={{color: '#0b4a2b', marginLeft : 50, fontSize : 50}}>DocConnect</div>
                <button className = "btn" style = {{marginLeft : 1000}} type="submit" id='btn-log-out' onClick={(e) => onLogOutSubmit(e)}>
                    Log out
                </button>
            </div>
            <div className="container-2">
            <Appointment appointments={appointments}
            onClick = {writeThings} />
            </div>
           
        </div>
    )

}


export default DoctorAppointmentsPage
